mapper.insert:
INSERT INTO
    RENTAL
    (
        prd_id
      , lender_email
      , borrower_email
      , rental_start_date
      , rental_end_date
      , rent_status
    )
    VALUES
    (
        #{id}
      , #{lenderEmail}
      , #{email}
      , #{rentalStartDate}
      , #{rentalEndDate}
      , '01'
    )

mapper.update:
UPDATE
       RENTAL
   SET
       RENT_STATUS = "02"
 WHERE
       PRD_ID = #{id}
   AND LENDER_EMAIL = #{email}
   AND BORROWER_EMAIL = #{borrowerEmail}
   AND RENTAL_START_DATE = #{rentalStartDate}
   AND RENTAL_END_DATE = #{rentalEndDate}

UPDATE
    RENTAL
SET
    RENT_STATUS = "04"
WHERE
    PRD_ID = #{id}
  AND LENDER_EMAIL = #{email}
  AND BORROWER_EMAIL = #{borrowerEmail}
  AND RENTAL_START_DATE = #{rentalStartDate}
  AND RENTAL_END_DATE = #{rentalEndDate}
